tinymce.PluginManager.add("youtube",function(e,t){e.addButton("youtube",{tooltip:"Add YouTube Video",icon:"media",onclick:function(){e.windowManager.open({title:"YouTube Plugin",body:[{type:"textbox",name:"vid",label:"Video ID"}],onsubmit:function(t){if(t.data.code){e.insertContent('<iframe width="420" height="315" '+'src="http://www.youtube.com/embed/'+t.data.vid+'" frameborder="0" '+"allowfullscreen></iframe>")}else{tinyMCE.activeEditor.windowManager.alert("Please fill the code field to use YouTube plugin.")}}})}});e.addMenuItem("youtube",{text:"Add YouTube Video",context:"tools",onclick:function(){e.windowManager.open({title:"TinyMCE site",url:"http://www.tinymce.com",width:800,height:600,buttons:[{text:"Close",onclick:"close"}]})}})})