tinymce.PluginManager.add("youtube",function(e,t){e.addButton("youtube",{tooltip:"Add YouTube Video",icon:"media",onclick:function(){e.windowManager.open({title:"YouTube Plugin",body:[{type:"textbox",name:"vid",label:"Video ID"},{type:"textbox",name:"vw",label:"Width"},{type:"textbox",name:"vh",label:"Height"}],onsubmit:function(t){var n,r;if(t.data.vw){n=t.data.vw}else{n=400}if(t.data.vh){r=t.data.vh}else{r=300}if(t.data.vid){e.insertContent('<object width="'+n+'" height="'+r+'"><param name="movie" value="https://www.youtube.com/v/'+t.data.vid+'"></param><param name="allowScriptAccess" value="always"></param><embed src="https://www.youtube.com/v/'+t.data.vid+'" type="application/x-shockwave-flash" allowscriptaccess="always" width="'+n+'" height="'+r+'"></embed></object>')}else{tinyMCE.activeEditor.windowManager.alert("Please fill the Video ID field to use YouTube plugin")}}})}})})